function toggleMenu(){document.querySelector(".nav__menu").classList.toggle("active")}function setLanguage(e){fetch(`languages/${e}.json`).then(e=>e.json()).then(n=>{console.log("Translations loaded:",n),document.querySelectorAll("[data-translate]").forEach(e=>{var t=e.getAttribute("data-translate");n[t]&&(console.log(`Translating ${t} to: `+n[t]),"IMG"===e.tagName?e.alt=n[t]:e.textContent=n[t])})}).catch(e=>console.error("Error loading language file:",e))}window.onload=function(){document.querySelector(".nav__logo").style.animation="fadeInLogo 1s forwards",document.querySelector(".nav__menu").style.animation="slideInMenu 1s 0.5s forwards",document.querySelector(".nav__user").style.animation="fadeInUser 1s 1s forwards";let t=document.querySelectorAll(".nav__language-switcher button");t.forEach(e=>{e.addEventListener("click",()=>{t.forEach(e=>e.classList.remove("active")),e.classList.add("active")})})},document.addEventListener("DOMContentLoaded",function(){var e=document.querySelector(".nav__burger");let t=document.querySelector(".nav__menu");e.addEventListener("click",function(){t.classList.toggle("nav__menu--active")})}),document.querySelectorAll(".nav__item > a").forEach(e=>{e.addEventListener("click",function(e){var t=this.nextElementSibling;t&&t.classList.contains("submenu")&&(e.preventDefault(),t.classList.toggle("active"))})});